import { useState, useEffect } from 'react';

import './admin.css';
const AdminPage = () => {
    const [api, setApi] = useState("my_work")
    const [myWorks, setMyWork] = useState([]);

    const [data, setData] = useState({
        image:"",
        title:"",
        price:"",
    })

    const handleChangeApi = (event) => {
        setApi(event.target.value);
        console.log(event.target.value);
      };

  useEffect(() => {
   fetch('http://localhost:3000/' + api)
    .then((res) => res.json())
    .then((resJson) => {
      setMyWork(resJson);
    })
  },[api]);
  
  const handleCreateData = (event) => {
    event.preventDefault();
      console.log(data);
  }
  const handleOnchangeData = (event) => {
        setData((prev) => {
            return{
                ...prev,
                [event.target.name] : event.target.value,
            }
           
        });
  };

  
    // useEffect((data) => {
    //     console.log(data);
    //     const options = { 
    //         method : 'POST',
    //         headers: {
    //           'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify(data)
    //     }
    //     fetch('http://localhost:3000/' + api,options)
    // },[api]);   
  

  
    return (
        <div>
            <h1>Trang quản lí sản phẩm</h1>
           <div>
               <label>Thay đổi danh sách sản phẩm cần chỉnh sửa</label>
           <select onChange={handleChangeApi} value={api}>
                <option value="my_work">my_work</option>
                <option value="product">product</option>
                <option value="capynbara">capynbara</option>
            </select>
           </div>
            
            <h2>Danh sách sản phẩm</h2>
            <br/>
            <div>
        <table id="list-sanpham">
            <tr>
                <td>ID</td>
                <td>Tên sản phẩm</td>
                <td>Ảnh sản phẩm</td>
                <td>Giá sản phẩm</td>
                <td>Action</td>
            </tr>
            <tbody>
                {myWorks.map((myWork) => {
                    return (
                    <tr key={myWork.id}>
                    <td>{myWork.id}</td>
                    <td>{myWork.title}</td>
                    <td><img src={myWork.image} /></td>
                    <td>{myWork.price}</td>
                    
                </tr>
                    )
                })}
                
            </tbody>
        </table>
    </div>
    <form class="control" onSubmit={handleCreateData}>
        <input 
        type="text" 
        placeholder="Tên sản phẩm" 
        name="title" 
        value={data.title} 
        onChange={handleOnchangeData}
        />
        <input 
        type="text" 
        placeholder="ảnh sản phẩm" 
        name="image" 
        value={data.image} 
        onChange={handleOnchangeData}
        
        />
        <input 
        type="number" 
        placeholder="giá sản phẩm" 
        name="price"
        value={data.price}
        onChange={handleOnchangeData}
        
        />
        <button>Create</button>
        <button id="btn-edit">Cập nhật</button>
    </form>
            
        </div>
    );
};
export default AdminPage;
