import { useState, useEffect } from 'react';

import './admin.css';
const AdminPage = () => {
    const [api, setApi] = useState("my_work")
    const [myWorks, setMyWork] = useState([]);

  

  useEffect(() => {
   fetch('http://localhost:3000/' + api)
    .then((res) => res.json())
    .then((resJson) => {
      setMyWork(resJson);
    })
  },[api]);
  
  const handleChangeApi = (event) => {
    setApi(event.target.value);
    console.log(event.target.value);
  };
  
    return (
        <div>
            <h1>Trang quản lí sản phẩm</h1>
           
            <select onchange={handleChangeApi} value={api}>
                <option value="my_work">my_work</option>
                <option value="product">product</option>
                <option value="capynbara">capynbara</option>
            </select>
            <h2>Danh sách sản phẩm</h2>
            <br/>
            <div>
        <table id="list-sanpham">
            <tr>
                <td>ID</td>
                <td>Tên sản phẩm</td>
                <td>Ảnh sản phẩm</td>
                <td>Giá sản phẩm</td>
                <td>Action</td>
            </tr>
            <tbody>
                {myWorks.map((myWork) => {
                    return (
                    <tr key={myWork.id}>
                    <td>{myWork.id}</td>
                    <td>{myWork.title}</td>
                    <td><img src={myWork.image} /></td>
                    <td>{myWork.price}</td>
                    
                </tr>
                    )
                })}
                
            </tbody>
        </table>
    </div>
    <div class="control">
        <input type="text" placeholder="Tên sản phẩm" />
        <input type="text" placeholder="ảnh sản phẩm" />
        <input type="number" placeholder="giá sản phẩm" />
        <button id="btn">Add</button>
        <button id="btn-edit">Cập nhật</button>
    </div>
            
        </div>
    );
};
export default AdminPage;
